(function() {
  var fs, wordlist, wordlist_file;

  fs = require('fs');

  wordlist_file = require.resolve('./wordlist.json');

  wordlist = JSON.parse(fs.readFileSync(wordlist_file, 'utf8'));

  module.exports = function(l) {
    var i, words;
    words = (function() {
      var _results;
      _results = [];
      for (i = 1; 1 <= l ? i <= l : i >= l; 1 <= l ? i++ : i--) {
        _results.push(wordlist.words[Math.floor(Math.random() * wordlist.words.length)]);
      }
      return _results;
    })();
    return words.join(' ');
  };

}).call(this);
